mapper.select:
select
count(a.no)
from post a, category b
where a.category_no=b.no
and b.blog_id=#{blogId}
and a.category_no=#{pathNo1}

select aa.nn as num
from
(SELECT @rownum:=@rownum+1 as nn, a.no
FROM post a, category b, (SELECT @rownum:=0) TMP
where a.category_no=b.no
and b.blog_id=#{blogId}
and a.category_no=#{pathNo1}
order by no desc) aa
where no=#{no}
